package JavaBase;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	//premitive datatypes-int,double,char,boolean
		int i =10;
		System.out.println(i);
	//	int i=20;---- duplicate variables are not allowed
		i =20;///replace the value
	//double
		double d=12.33;
		System.out.println(d);
		double d1=100;//100.00
	//char single digit value
		char c='a';
		System.out.println(c);
		char c1='A'; 
		//char c2='aa'; not allowed only single character allowed
	//boolean
		boolean b1=true;
		System.out.println(b1);
		boolean b2=false;
	//String : is a class, not a datatype---we can use as datatype
		String s1="selenium";
		System.out.println(s1);
		String s2="1000";
		String s3="12.33";
	}

}
